port&amp;#x3D;8080 
# 配置项服务器名称
server_name&amp;#x3D; &amp;quot;这是一个config库的demo&amp;quot;

#配置项数据库URL
database_url&amp;#x3D;&amp;quot;0.0.0.0&amp;quot;
# 配置项数据库端口
database_port&amp;#x3D;&amp;quot;7890&amp;quot;
# 配置项数据库用户名
database_user&amp;#x3D;&amp;quot;root&amp;quot;
