    address: &amp;quot;0.0.0.0&amp;quot;,
    port: 8080,
    server_name: &amp;quot;这是一个config库的demo&amp;quot;,
    database_url: &amp;quot;0.0.0.0&amp;quot;,
    database_port: 7890,
    database_user: &amp;quot;root&amp;quot;,
